import React from 'react';
import {Row, Col } from 'react-bootstrap';
//import {Parallax, Background } from 'react-parallax'
import ss from '../seeingSounds.png';
import ReactCardFlip from 'react-card-flip';
import {useState} from 'react';
//import '../assets/fonts/Myfont-reg.ttf';


export default function Albums(props) {
    const [flipped, setFlipped] = useState(false)
    function handleClick(e) {
        e.preventDefault();
        console.log("clicked");
        if(flipped){
            setFlipped(false);
        }
        else
            setFlipped(true);
    }
    var x = props.musicItem.lyric.replace(/,/g,'<br/>');
    console.log(x);
    function createLyric(){
        return {__html: x}
    }
    return (
            <ReactCardFlip isFlipped={flipped} flipDirection="horizontal" >
                <Row style={{alignItems: 'center'}} >
                    <Col><h5 dangerouslySetInnerHTML={createLyric()} />
                    </Col>

                    <Col>
                        <Row>
                            <Col>
                                <img style={{height:'80vh'}} src={props.musicItem.albumReq} onClick={handleClick}/>
                            </Col>
                        </Row>
                    </Col>

                    <Col className="myText">{props.musicItem.details}</Col>
                </Row>
                <Row style={{alignItems: 'center'}} onClick={handleClick}>
                    <Col >{props.musicItem.lyric}</Col>

                    <Col >
                        <Row>
                            <Col style={{height:'80vh'}}>
                                {/* <img style={{height:'50vh'}} src={ss} onClick={handleClick}/> */}
                            </Col>
                        </Row>
                    </Col>

                    <Col className="myText">{props.musicItem.details}</Col>
                </Row>
            </ReactCardFlip>

    )
}
