import React from 'react';
import {Row, Col } from 'react-bootstrap';
//import {Parallax, Background } from 'react-parallax'
import ss from '../seeingSounds.png';
import ReactCardFlip from 'react-card-flip';
import {useState} from 'react';


export default function Albums(props) {
    const [flipped, setFlipped] = useState(false)
    function handleClick(e) {
        e.preventDefault();
        if(flipped){
            setFlipped(false);
        }
        else
            setFlipped(true);
        setFlipped()
    }
    return (
            <ReactCardFlip isFlipped={flipped} flipDirection="horizontal" onClick={handleClick}>
                <Row style={{alignItems: 'center'}}>
                    <Col>{props.musicItem.lyric}</Col>

                    <Col>
                        <Row>
                            <Col>
                                <img style={{height:'50vh'}} src={props.musicItem.albumReq}/>
                            </Col>
                        </Row>
                    </Col>

                    <Col>{props.musicItem.details}</Col>
                </Row>
                <Row style={{alignItems: 'center'}}>
                    <Col>{props.musicItem.lyric}</Col>

                    <Col>
                        <Row>
                            <Col>
                                <img style={{height:'50vh'}} src={ss}/>
                            </Col>
                        </Row>
                    </Col>

                    <Col>{props.musicItem.details}</Col>
                </Row>
            </ReactCardFlip>

    )
}
